
const numberCarriers = {
    'mtn' : [ '0803','0816','0903','0810','0806','0703','0706','0813','0814','0906' ],
    'glo': ['0805','0905','0807','0811','0705','0815'],
    'etisalat':[ '0908','0909','0818','0809','0817'],
    'airtel':['0907','0708','0802','0902','0812','0808','0701']
  }
  
  const mtnLogo = 'data:image/png;base64,'
  const gloLogo = 'data:image/jpeg;base64,'
  const airtelLogo = 'data:image/png;base64,'
  const etisalatLogo = 'data:image/png;base64,'

  
  function checkProvider(){
    const inputNumber = document.getElementById('phone_outlined').value;
    const strInputNumber = inputNumber.toString();
    let prefix ;
  
    if(strInputNumber.startsWith('+234')){
  
      prefix = '0'+strInputNumber.slice(4,7);
  
    }else if(strInputNumber.startsWith('0')){
  
      prefix = strInputNumber.slice(0,4);
  
    }else{
        console.log('INVALID NUMBER');
    }
    for(const carrier in numberCarriers){
        for(let index = 0 ; index < numberCarriers[carrier].length ; index++){
            if(numberCarriers[carrier][index] === prefix){
                const imgLogo = document.getElementById('carrier')
                switch(carrier){
                    case 'airtel':
                        imgLogo.style.backgroundImage= `url(${airtelLogo})`
                        break
                    case 'etisalat':
                        imgLogo.style.backgroundImage= `url(${etisalatLogo})`
                        break
                    case 'glo':
                        imgLogo.style.backgroundImage= `url(${gloLogo})`
                        break
                    case 'mtn':
                        imgLogo.style.backgroundImage= `url(${mtnLogo})`
                        break
                }
                console.log(`Hello, your number belong to ${carrier}`)
            }
        }
    }
  }
  
  
  
  const numberForm = document.getElementById('phone_outlined');
  numberForm.addEventListener('keyup', checkProvider)
  
  
  
  
  
 function handleSubmit(event){
    event.preventDefault()
    const username = event.target.username_outlined.value
    const password1 = event.target.password_outlined.value
    const password2 = event.target.password2_outlined.value
    if( password1 === password2 ){
        if( password1.length > 8 ){
            alert(`${username} thanks for the time taking to fill the form. Bless you`)
        }else{
            document.getElementById('password-error').innerText = 'Password must atleast be 8 character long'
        }

    }else{
        document.getElementById('password-error').innerText = 'Password does not match'
    }
 }


 const form = document.getElementById('phone_form');
form.addEventListener('submit', handleSubmit)